mapper.select:
select rt.id, rt.name, count(oi.id) as count from space_info rt left join park_order_info oi on (rt.id = oi.space_id
AND DATE_FORMAT(oi.pay_date, '%Y') = #{year}
AND DATE_FORMAT(oi.pay_date, '%c') = #{month}
AND oi.status = '1' ) where 1 = 1 group by rt.id

select rt.id, rt.name, IFNULL(SUM(oi.total_price), 0) AS count from space_info rt left join park_order_info oi on (rt.id = oi.space_id
AND DATE_FORMAT(oi.pay_date, '%Y') = #{year}
AND DATE_FORMAT(oi.pay_date, '%c') = #{month}
AND oi.status = '1' ) where 1 = 1 group by rt.id

select tempDay.date, IFNULL(SUM(oi.total_price), 0) AS price from ( select date from ( select DATE_FORMAT(DATE_SUB(last_day(#{checkDate}), interval xc-1 day), '%Y-%m-%d') as date from ( select @xi := @xi + 1 as xc from ( select 1 union select 2 union select 3 union select 4 union select 5 union select 6) xc1, ( select 1 union select 2 union select 3 union select 4 union select 5 union select 6) xc2, ( select @xi := 0) xc0 ) xcxc) x0 where x0.date >= ( select date_add(#{checkDate}, interval-day(#{checkDate})+ 1 day))) as tempDay left join park_order_info oi on (DATE_FORMAT( oi.pay_date, '%Y-%m-%d' ) = DATE_FORMAT( tempDay.date, '%Y-%m-%d')
AND DATE_FORMAT(oi.pay_date, '%Y') = #{year}
AND DATE_FORMAT(oi.pay_date, '%c') = #{month}
AND oi.status = '1' ) where 1 = 1 group by tempDay.date

select tempDay.date, count(oi.id) as count from ( select date from ( select DATE_FORMAT(DATE_SUB(last_day(#{checkDate}), interval xc-1 day), '%Y-%m-%d') as date from ( select @xi := @xi + 1 as xc from ( select 1 union select 2 union select 3 union select 4 union select 5 union select 6) xc1, ( select 1 union select 2 union select 3 union select 4 union select 5 union select 6) xc2, ( select @xi := 0) xc0 ) xcxc) x0 where x0.date >= ( select date_add(#{checkDate}, interval-day(#{checkDate})+ 1 day))) as tempDay left join park_order_info oi on (DATE_FORMAT( oi.pay_date, '%Y-%m-%d' ) = DATE_FORMAT( tempDay.date, '%Y-%m-%d')
AND DATE_FORMAT(oi.pay_date, '%Y') = #{year}
AND DATE_FORMAT(oi.pay_date, '%c') = #{month}
AND oi.status = '1' ) where 1 = 1 group by tempDay.date
